mapper.select:
SELECT COUNT(*)
FROM stu_student
WHERE deleted = 0
  AND status = #{status}

    AND campus_id = #{campusId}

SELECT status as name, COUNT(*) as value FROM stu_student WHERE deleted = 0
AND campus_id = #{campusId}
GROUP BY status

SELECT DATE(pay_time) as date, COALESCE(SUM(amount), 0) as amount FROM fin_payment WHERE deleted = 0 AND status = 'paid' AND pay_time >= DATE_SUB(CURDATE(), INTERVAL #{days} DAY)
AND campus_id = #{campusId}
GROUP BY DATE(pay_time) ORDER BY date

SELECT COUNT(*)
FROM tch_schedule
WHERE deleted = 0
  AND schedule_date = #{date}
  AND status != 'cancelled'

    AND campus_id = #{campusId}

SELECT COUNT(*)
FROM tch_schedule
WHERE deleted = 0
  AND schedule_date BETWEEN #{startDate} AND #{endDate}
  AND status != 'cancelled'

    AND campus_id = #{campusId}

SELECT COUNT(*)
FROM tch_class
WHERE deleted = 0
  AND status = #{status}

    AND campus_id = #{campusId}

SELECT source as name, COUNT(*) as value FROM mkt_lead WHERE deleted = 0
AND campus_id = #{campusId}
GROUP BY source
